"use client";

import * as React from "react";

export interface CustomSliderProps {
  value: number;
  min: number;
  max: number;
  step: number;
  onChange: (value: number) => void;
  label: string;
  valueLabel: string;
  accentColor?: string;
  labelColor?: string;
  trackColor?: string;
  thumbColor?: string;
  thumbActiveColor?: string;
  trackHeight?: number;
  thumbWidth?: number;
  thumbWidthActive?: number;
  thumbHeight?: number;
  gap?: number;
}

export function CustomSlider({
  value,
  min,
  max,
  step,
  onChange,
  label,
  valueLabel,
  accentColor = "#F46066",
  labelColor = "#AD5256",
  trackColor = "#52525b",
  thumbColor = "#c9c9c9",
  thumbActiveColor = "#ffffff",
  trackHeight = 10,
  thumbWidth = 4,
  thumbWidthActive = 6,
  thumbHeight = 14,
  gap = 6,
}: CustomSliderProps) {
  const [isGrabbing, setIsGrabbing] = React.useState(false);
  const percentage = ((value - min) / (max - min)) * 100;
  const currentThumbWidth = isGrabbing ? thumbWidthActive : thumbWidth;
  const trackGap = currentThumbWidth / 2 + gap;

  return (
    <div className="flex flex-col gap-2">
      <label className="text-sm font-mono">
        <span style={{ color: labelColor }}>{label}</span>
        <span style={{ color: accentColor }}>{valueLabel}</span>
      </label>
      <div className="relative h-5 flex items-center">
        <div
          className="absolute left-0 rounded-[2px]"
          style={{
            width: `calc(${percentage}% - ${trackGap}px)`
,
            height: trackHeight,
            backgroundColor: accentColor,
            transition: "width 0.35s cubic-bezier(0.18, 1.4, 0.3, 1)",
          }}
        />
        <div
          className="absolute right-0 rounded-[2px]"
          style={{
            width: `calc(${100 - percentage}% - ${trackGap}px)`
,
            height: trackHeight,
            backgroundColor: trackColor,
            transition: "width 0.4s cubic-bezier(0.2, 1.5, 0.32, 1)",
          }}
        />
        <div
          className="absolute rounded-full pointer-events-none"
          style={{
            left: `calc(${percentage}% - ${currentThumbWidth / 2}px)`
,
            width: currentThumbWidth,
            height: thumbHeight,
            backgroundColor: isGrabbing ? thumbActiveColor : thumbColor,
            transform: isGrabbing ? "scaleY(1.1)" : "scaleY(1)",
            transition:
              "left 0.08s ease-out, width 0.2s cubic-bezier(0.34, 1.56, 0.64, 1), background-color 0.1s ease, transform 0.2s cubic-bezier(0.34, 1.56, 0.64, 1)",
          }}
        />
        <input
          type="range"
          min={min}
          max={max}
          step={step}
          value={value}
          onChange={(e) => onChange(Number(e.target.value))}
          onMouseDown={() => setIsGrabbing(true)}
          onMouseUp={() => setIsGrabbing(false)}
          onMouseLeave={() => setIsGrabbing(false)}
          onTouchStart={() => setIsGrabbing(true)}
          onTouchEnd={() => setIsGrabbing(false)}
          className="absolute inset-0 w-full opacity-0 cursor-grab active:cursor-grabbing"
        />
      </div>
    </div>
  );
}

vanillaCode.css:
.custom-slider {
  display: flex;
  flex-direction: column;
  gap: 8px;
}
.slider-label {
  font-family: monospace;
  font-size: 14px;
}
.slider-label .label-text { color: #AD5256; }
.slider-label .label-value { color: #F46066; }
.slider-track-container {
  position: relative;
  height: 20px;
  display: flex;
  align-items: center;
}
.slider-track-filled {
  position: absolute;
  left: 0;
  height: 10px;
  background: #F46066;
  border-radius: 2px;
  transition: width 0.35s cubic-bezier(0.18, 1.4, 0.3, 1);
}
.slider-track-unfilled {
  position: absolute;
  right: 0;
  height: 10px;
  background: #52525b;
  border-radius: 2px;
  transition: width 0.4s cubic-bezier(0.2, 1.5, 0.32, 1);
}
.slider-thumb {
  position: absolute;
  width: 4px;
  height: 14px;
  background: #c9c9c9;
  border-radius: 9999px;
  pointer-events: none;
  transition: left 0.08s ease-out,
    width 0.2s cubic-bezier(0.34, 1.56, 0.64, 1),
    background-color 0.1s ease,
    transform 0.2s cubic-bezier(0.34, 1.56, 0.64, 1);
}
.slider-thumb.grabbing {
  width: 6px;
  background: #ffffff;
  transform: scaleY(1.1);
}
.slider-input {
  position: absolute;
  inset: 0;
  width: 100%;
  opacity: 0;
  cursor: grab;
}
.slider-input:active { cursor: grabbing; }

vanillaCode.js:
const slider = document.querySelector('.custom-slider');
const input = slider.querySelector('.slider-input');
const filled = slider.querySelector('.slider-track-filled');
const unfilled = slider.querySelector('.slider-track-unfilled');
const thumb = slider.querySelector('.slider-thumb');
const valueLabel = slider.querySelector('.label-value');

const gap = 6;
const thumbWidth = 4;
const thumbWidthActive = 6;

function update() {
  const min = Number(input.min);
  const max = Number(input.max);
  const val = Number(input.value);
  const pct = ((val - min) / (max - min)) * 100;
  const isGrabbing = thumb.classList.contains('grabbing');
  const tw = isGrabbing ? thumbWidthActive : thumbWidth;
  const trackGap = tw / 2 + gap;

  filled.style.width = 'calc(' + pct + '% - ' + trackGap + 'px)';
  unfilled.style.width = 'calc(' + (100 - pct) + '% - ' + trackGap + 'px)';
  thumb.style.left = 'calc(' + pct + '% - ' + (tw / 2) + 'px)';
  valueLabel.textContent = val;
}

input.addEventListener('input', update);
input.addEventListener('mousedown', () => {
  thumb.classList.add('grabbing');
  update();
});
input.addEventListener('mouseup', () => {
  thumb.classList.remove('grabbing');
  update();
});
input.addEventListener('mouseleave', () => {
  thumb.classList.remove('grabbing');
  update();
});

update();

