import * as React from "react";
import { CustomSlider } from "@stashcode/ui";

export function Example() {
  const [value, setValue] = React.useState(50);
  return (
    <CustomSlider
      value={value}
      min={0}
      max={100}
      step={1}
      onChange={setValue}
      label="Volume:"
      valueLabel={String(value)}
    />
  );
}

